var Game = function () {
};


Game.prototype.getInfo=function (gameCode){
    var res;
    switch(gameCode)
    {
        case 'T01':
            res='大乐透';
            break;
        case 'T05':
            res='11选5';
            break;
        case 1200:
            res='images/bonus.png';
            break;
        case 1300:
            res='images/wrong.png';
            break;
        case 1900:
            res='images/unline.png';
            break;
        default:
            res='images/wrong.png';
    }
    return res;
};


var game = new Game();

module.exports = game;