var SocketControl = function (io, socket) {
    var self = this;
    self.io = io;
    self.socket = socket;
};


SocketControl.prototype.init = function () {
    var self = this;
    self.socket.on('hellow', self.hellow);

};

SocketControl.prototype.hellow = function (data) {
    var socket = this;
    console.log(socket.id + 'says:' + data);
};


module.exports = SocketControl;
